INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165925, 'Cisapride', 'Simvastatin', 'Moderate', 'The coadministration with simvastatin may increase the plasma concentrations of cisapride due to competitive inhibition of cisapride metabolism via CYP450 3A4 isoenzymes. Conversely, cisapride may reduce the plasma concentrations of the pharmacologically active acid metabolite of simvastatin, possibly due to reduction of its reabsorption during enterohepatic recycling secondary to increased gastrointestinal motility.', NULL, 'Due to the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, an HMG-CoA reductase inhibitor that is not metabolized by CYP450 3A4 such as fluvastatin or pravastatin may be preferable in patients receiving concomitant therapy with cisapride.', NULL, 'Due to the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, an HMG-CoA reductase inhibitor that is not metabolized by CYP450 3A4 such as fluvastatin or pravastatin may be preferable in patients receiving concomitant therapy with cisapride. If simvastatin must be used, clinicians should monitor patients for cisapride toxicity as well as reduced cholesterol-lowering effects of simvastatin. Patients should be advised to seek medical attention if they experience fainting, dizziness, an irregular heartbeat or pulse, or other unusual symptoms while using cisapride.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166098/', '[1] "Product Information. Propulsid (cisapride)." Janssen Pharmaceutica, Titusville, NJ.[2] Simard C, OHara GE, Prevost J, Guilbaud R, Masse R, Turgeon J "Study of the drug-drug interaction between simvastatin and cisapride in man." Eur J Clin Pharmacol 57 (2001): 229-34[3] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[4] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[5] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on serum concentrations of simvastatin, simvastatin acid, and HMG-CoA reductase inhibitors." Clin Pharmacol Ther 64 (1998): 477-83[6] "Product Information. Mevacor (lovastatin)." Merck & Co, Inc, West Point, PA.[7] Thompson PD, Clarkson P, Karas RH "Statin-associated myopathy." JAMA 289 (2003): 1681-90[8] "Product Information. Zocor (simvastatin)." Merck & Co, Inc, West Point, PA.[9] Kantola T, Kivisto KT, Neuvonen PJ "Grapefruit juice greatly increases serum concentrations of lovastatin and lovastatin acid." Clin Pharmacol Ther 63 (1998): 397-402[10] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[11] Lewin MB, Bryant RM, Fenrich AL, Grifka RG "Cisapride-induced long QT interval." J Pediatr 128 (1996): 279-81[12] Dresser GK, Spence JD, Bailey DG "Pharmacokinetic-pharmacodynamic consequences and clinical relevance of cytochrome P450 3A4 inhibition." Clin Pharmacokinet 38 (2000): 41-57[13] Kivisto KT, Lilja TJ, Backman JT, Neuvonen PJ "Repeated consumption of grapefruit juice considerably increases plasma concentrations of cisapride." Clin Pharmacol Ther 66 (1999): 448-53[14] Michalets EL, Williams CR "Drug interactions with cisapride: clinical implications." Clin Pharmacokinet 39 (2000): 49-75[15] Hill SL, Evangelista JK, Pizzi AM, Mobassaleh M, Fulton DR, Berul CI "Proarrhythmia associated with cisapride in children." Pediatrics 101 (1998): 1053-6[16] Bran S, Mur