INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165953, 'Fostamatinib', 'Simvastatin', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166126/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165954, 'Fostemsavir', 'Simvastatin', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of most HMG-CoA reductase inhibitors (i.e., statins) to varying degrees. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1/1B3-mediated hepatic uptake by temsavir, the active moiety of fostemsavir. Inhibition of breast cancer resistance protein (BCRP)-mediated intestinal and hepatic transport of statins may also contribute.', NULL, 'The lowest effective dosage of a statin should be used when prescribed with fostemsavir, and the dosage titrated cautiously based on clinical response and adverse events.', NULL, 'The lowest effective dosage of a statin should be used when prescribed with fostemsavir, and the dosage titrated cautiously based on clinical response and adverse events. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166127/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice increases serum concentrations of atorvastatin and has no effect on pravastatin." Clin