    ) VALUES (1423279, 'Procaine', 'Laronidase', 'Minor', 'The concomitant administration of chloroquine or procaine may theoretically reduce the therapeutic efficacy of laronidase. These lysosomotropic amines may interfere with the intracellular uptake of laronidase by lysosomes. The clinical significance is unknown.', NULL, '-', NULL, '-', 'Distribution', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
