    ) VALUES (1423296, 'Cimetidine', 'Ledipasvir', 'Moderate', 'Coadministration of ledipasvir with H2-receptor antagonists may reduce its gastrointestinal absorption. Ledipasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', NULL, 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from ledipasvir-sofosbuvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', NULL, 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from ledipasvir-sofosbuvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
