INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166383, 'Sodium bicarbonate', 'Bacampicillin', 'Moderate', 'Coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents with acid neutralizing or reducing effects may decrease the oral bioavailability and plasma concentrations of ampicillin from bacampicillin. The exact mechanism of interaction is unknown but may be related to degradation of the prodrug when gastric pH is increased.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, 'The possibility of a reduced or subtherapeutic response to bacampicillin should be considered during coadministration with antacids, H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. Preferably, these agents should be avoided during therapy with bacampicillin, or an alternative antibiotic be prescribed if these medications cannot be discontinued. Patients treated with antacids (or oral medications that contain antacids such as didanosine buffered tablets or pediatric oral solution) may minimize the effects of the interaction with bacampicillin by separating the times of administration by at least 2 hours.', 'Absorption', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin, Calcium chloride, Potassium chloride, Magnesium sulfate, Sodium acetate, More', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, Nafcillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166556/', '[1] Honig PK, Gillespie BK "Clinical significance of pharmacokinetic drug interactions with over-the-counter (OTC) drugs." Clin Pharmacokinet 35 (1998): 167-71[2] Sommers DK, van Wyk M, Moncrieff J, Schoeman HS "Influence of food and reduced gastric acidity on the bioavailability of bacampicillin and cefuroxime axetil." Br J Clin Pharmacol 18 (1984): 535-9', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166384, 'Cabotegravir', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: Coadministration with antacids may decrease the bioavailability of oral cabotegravir. The proposed mechanism likely involves a pH-dependent reduction in dissolution and absorption of cabotegravir, which is poorly soluble over a wide pH range. The interaction has not been studied specifically with antacids.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of cabotegravir, antacids or other medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) should be administered at least 2 hours before or 4 hours after the oral cabotegravir dose.', 'Absorption', 'Sorbitol, Sulfamethizole, Mannitol, Sodium chloride, Chlorhexidine, Neomycin, Calcium chloride, Potassium chloride, Sodium acetate, Arginine, Sodium glycerophosphate, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Zanamivir, Lenacapavir, Telaprevir, Etravirine, Cobicistat, Brincidofovir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166557/', '[1] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166385, 'Iron', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: The bioavailability of orally administered iron may be reduced by concomitant administration of antacids or other agents with acid-neutralizing effects. The exact mechanism is unknown but may involve reduced iron solubility due to increase in gastric pH and/or reduced absorption due to complexation or precipitation of the iron.', NULL, 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', NULL, 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', 'Absorption', 'Sorbitol, Sulfamethizole, Magnesium citrate, Sodium chloride, Mannitol, Chlorhexidine, Neomycin, Calcium chloride, Potassium chloride, Magnesium sulfate, Sodium acetate, More', 'Iron sucrose', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166558/', '[1] Gugler R, Allgayer H "Effects of antacids on the clinical pharmacokinetics of drugs. An update." Clin Pharmacokinet 18 (1990): 210-9[2] Coste JF, De Barbi VA, Keil LB, Needle MA "In-vitro interactions of oral hemantics and antacid suspensions." Curr Ther Res Clin Exp 22 (1977): 205-16[3] Coste JF, de Bari VA, Keil LB, Needle MA "In-vitro interactions of oral hematinics." Curr Ther Res Clin Exp 22 (1977): 205-15[4] Rastogi SP, Padilla F, Boyd CM "Effect of aluminum hydroxide on iron absorption." Kidney Int 8 (1975): 417[5] "Product Information. Suprep Bowel Prep Kit (magnes