INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166502, 'Solifenacin', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Alprostadil, Acetohydroxamic acid, Yohimbine, Avanafil, Phenyl salicylate, Tadalafil, Ammonium chloride, More', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Tolterodine, Phenazopyridine, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166675/', '[1] Vrolix M, Piessens J, De Geest H "Torsades de pointes after intracoronary papaverine." Eur Heart J 12 (1991): 273-6[2] Jain A, Jenkins MG "Intracoronary electro