INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166663, 'Belinostat', 'Sorafenib', 'Moderate', 'Coadministration with inhibitors of UGT1A1 may increase the plasma concentrations of belinostat, which is primarily (80% to 90%) metabolized by UGT1A1-mediated glucuronidation.', NULL, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided.', NULL, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided. Caution is advised when used with other agents that may also inhibit UGT1A1 such as nilotinib, pazopanib, regorafenib and sorafenib. Patients should be monitored closely for potentially increased adverse effects such as nausea, vomiting, diarrhea, thrombocytopenia, leukopenia (neutropenia and lymphopenia), anemia, infection, and hepatotoxicity.', 'Metabolism', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, More', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Porfimer sodium, Niraparib, Selinexor, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166836/', '[1] "Product Information. Beleodaq (belinostat)." Spectrum Pharmaceuticals Inc, Irvine, CA.', 1767369485);
