INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166824, 'Dimenhydrinate', 'Sotalol', 'Moderate', 'The use of higher than recommended dosages of diphenhydramine (e.g., through abuse or misuse) has been associated with serious and potentially fatal cardiac adverse events, including cardiac arrest, and arrhythmia related to prolongation of the QT interval. Coadministration with class Ia (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents.', NULL, 'Australian authorities recommend avoiding concomitant use of diphenhydramine and class Ia or class III antiarrhythmic agents. Additionally, patients should be counseled to not exceed the recommended dosage and frequency or duration of use of diphenhydramine, and to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Acebutolol, Penbutolol, Betaxolol, Carteolol, Acebutolol, Acetylsalicylic acid, Ivabradine', 'Ketotifen, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, Meclizine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166998/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Poluzzi E, Raschi E