INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166830, 'Ephedrine', 'Sotalol', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of ephedrine by blocking beta-1 adrenergic receptors in the heart. Noncardioselective beta-blockers can antagonize the bronchodilating effects of ephedrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree. The interaction is less likely to occur with cardioselective beta-blockers, which generally have little effect on beta-2 adrenergic receptors at therapeutic dosages.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations. Noncardioselective beta-blockers, including ophthalmic formulations, should generally be avoided in patients using ephedrine-containing preparations for bronchospastic diseases. If beta-blocker therapy is necessary, an agent with beta-1 selectivity (e.g., atenolol, metoprolol, betaxolol) is considered safer. However, caution is advised, especially with higher dosages of the beta-blocker.', 'Antagonism', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', 'Tropicamide, Ciclesonide, Mupirocin, Mometasone furoate, Mometasone, Flunisolide, Olopatadine, Vitamin A, Ipratropium, Cromoglicic acid, Hyaluronic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167004/', '[1] "Product Information. Ephedrine Sulfate (ephedrine)." Akorn Inc, Buffalo Grove, IL.', 1767369485);
