INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166927, 'Sotalol', 'Meclofenamic acid', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effect of beta-blockers. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Indomethacin and piroxicam have been reported to have greater attenuating effects than other NSAIDs, and indomethacin effects may be significant in patients with eclampsia.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving a beta-blocker who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Ivabradine', 'Misoprostol, Chondroitin sulfate, Esomeprazole, Glucosamine, Tolazoline, Dimethyl sulfoxide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167102/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs." Ann Intern Med 107 (1987): 628-35[2] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[3] Abate MA, Neely JL, Layne RD, D''Allessandri R "Interaction of indomethacin and sulindac with labetalol." Br J Clin Pharmacol 31 (1991): 363-6[4] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11[5] Wong DG, Spence JD, Lamki L, Freeman D, McDonald JW "Effect of non-steroidal anti-inflammatory drugs on control of hypertension by beta-blockers and diuretics." Lancet 1 (1986): 997-1001[6] Ylitalo P, Pitkajarvi T, Pyykonen ML, Nurmi AK, Seppala E, Vapaatalo H "Inhibition of prostaglandin synthesis by indomethacin interacts with the antihypertensive effect of atenolol." Clin Pharmacol Ther 38 (1985): 443-9[7] Durao V, Prata MM, Concalves LM "Modification of antihypertensive effect of B-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[8] Salvetti A, Arzilli F, Pedrinelli R, Beggi P, Motolese M "Interaction between oxprenolol and indomethacin on blood pressure in essential hypertensive patients." Eur J Clin Pharmacol 22 (1982): 197-201[9] Durao V, Prata MM, Goncalves LM "Modification of antihypertensive effect of beta-adrenoceptor-blocking agents by inhibition of endogenous prostaglandin synthesis." Lancet 2 (1977): 1005-7[10] Salvetti A, Pedrinelli R, Alberici P, Magagna A, Abdel-Haq B "The influence of indomethacin and sulindac on some pharmacological actions of atenolol in hypertensive patients." Br J Clin Pharmacol 17 Suppl 1 (1984): s108-11', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166928, 'Sotalol', 'Mephentermine', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of pressor agents by blocking beta-1 adrenergic receptors in the heart. Vasopressors may be less effective if the patient is receiving, or has recently received, a beta-blocking drug. In addition, peripheral vascular resistance may increase due to unopposed alpha-adrenergic effects of pressor agents in the presence of beta-blockade. Theoretically, the interaction may also occur with beta-blocker ophthalmic preparations, since they may be systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations.', NULL, 'No specific intervention is necessary, but clinicians should be alert to the potential for diminished cardiac response when pressor agents are used in patients treated with beta-blockers, including ophthalmic formulations. A beta-blocker such as propranolol may be used to treat cardiac arrhythmias that occur during administration of pressor agents like mephentermine, metaraminol, norepinephrine, or phenylephrine.', 'Antagonism', 'Carteolol, Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167103/', '[1] "Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company, Indianapolis, I