INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166935, 'Sotalol', 'Methoxyflurane', 'Moderate', 'The antihypertensive effects of methoxyflurane and beta blockers may be additive. The combination may cause profound hypotension. Older anesthetics such as methoxyflurane have more severe circulatory depressant effects in the presence of beta-blockade.', NULL, 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery.', NULL, 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery. However, in general, patients taking beta blockers should not be anaesthetized with methoxyflurane. Instead, an anesthetic with minimal negative inotropic activity may be considered. If methoxyflurane must be used, beta blocker therapy should be withdrawn gradually and generally completed 24 to 48 hours before anesthesia.', 'Synergism', 'Carteolol, Acetylsalicylic acid, Ivabradine', 'Ziconotide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167110/', '[1] "Product Information. Lopressor (metoprolol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Hunter JM "Synergism between halothane and labetalol." Anaesthesia 34 (1979): 257-9[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Scott DB, Buckley FP, Littlewood DG, Macrae WR, Arthur GR, Drummond GB "Circulatory effects of labetalol during halothane anaesthesia." Anaesthesia 33 (1978): 145-56', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (166936, 'Sotalol', 'Methyldopa', 'Moderate', 'Methyldopa and beta-blockers may have additive hypotensive effects. In addition, potentiation of hypertensive rebound associated with withdrawal of methyldopa or beta-blockers may occur if both drugs are withdrawn at the same time. The proposed mechanism may involve increased catecholamine release after methyldopa and/or a beta-blocker are withdrawn, which may lead to unopposed alpha-adrenergic effects and vasoconstriction.', NULL, 'Close monitoring of blood pressure is recommended during concomitant use, and if methyldopa or the beta blocker are withdrawn from therapy.', NULL, 'Close monitoring of blood pressure is recommended during concomitant use, and if methyldopa or the beta blocker are withdrawn from therapy. Methyldopa manufacturers recommend adjusting the beta blocker dose if methyldopa is added to therapy, and not exceeding a methyldopa dose of 500 mg/day when it is first added to therapy. Patients should be instructed to notify their doctor if they have a reduced heart rate, dizziness, fainting or headaches, chest pain or vision problems.', 'Synergism', 'Esmolol, Betaxolol, Ivabradine', 'Deserpidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167111/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Frishman WH "Beta-adrenergic receptor blockers. Adverse effects and drug interactions." Hypertension 11 (1988): II21-9[3] Reidenberg MM "Drug Discontinuation Effects are Part of the Pharmacology of a Drug." J Pharmacol Exp Ther (2011):[4] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/