INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423640, 'Dicoumarol', 'Levocarnitine', 'Moderate', 'Limited data suggest that levocarnitine may potentiate the hypoprothrombinemic effects of warfarin and other coumarin anticoagulants. The mechanism of interaction has not been established.', 'DDInter', 1767317476, 'Caution is advised when coumarin anticoagulants are used with levocarnitine. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of levocarnitine. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised when coumarin anticoagulants are used with levocarnitine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
