INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423682, 'Imatinib', 'Levothyroxine', 'Moderate', 'Imatinib has been reported to induce clinical hypothyroidism in patients receiving levothyroxine. The exact mechanism is unknown but may involve stimulation of the non-deiodination clearance of T4 and T3 by imatinib.', 'DDInter', 1767317476, 'Monitoring of thyroid function including laboratory serum TSH concentrations should be performed before, during, and after any dosage change or discontinuation of imatinib therapy, and the levothyroxine dosage adjusted accordingly. The authors of the study cited above suggest a minimum 2-fold increase in levothyroxine therapy before initiation of imatinib treatment and close monitoring of thyroid function during treatment.', 'Others', 'Monitoring of thyroid function including laboratory serum TSH concentrations should be performed before, during, and after any dosage change or discontinuation of imatinib therapy, and the levothyroxine dosage adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423683, 'Imipramine', 'Levothyroxine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 1767317476, 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423684, 'Indinavir', 'Levothyroxine', 'Moderate', 'A single case report suggests that indinavir may increase serum thyroxine concentrations, potentially resulting in hyperthyroidism. The proposed mechanism is indinavir inhibition of glucuronosyltransferase, which is partially responsible for the metabolic clearance of thyroxine.', 'DDInter', 1767317476, 'During coadministration with indinavir, the possibility of an enhanced therapeutic response to levothyroxine should be considered. Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever indinavir is added to or withdrawn from therapy.', 'Metabolism', 'During coadministration with indinavir, the possibility of an enhanced therapeutic response to levothyroxine should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
