INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423717, 'Methimazole', 'Levothyroxine', 'Moderate', 'Antithyroid agents such as methimazole and propylthiouracil can affect the therapeutic response to thyroid hormone therapy. These agents decrease thyroid hormone secretion, which may result in hypothyroidism. Conversely, thyroid hormone therapy can antagonize the pharmacologic effects of antithyroid agents by supplying an exogenous source of the hormone.', 'DDInter', 1767317476, 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', 'Antagonism', 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
