INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423754, 'Secobarbital', 'Levothyroxine', 'Moderate', 'Barbiturates may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. Clinical data are limited.', 'DDInter', 1767317476, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of thyroid replacement dosage may be indicated when initiating or discontinuing barbiturate therapy.', 'Metabolism', 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
