INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423760, 'Iodide I-131', 'Levothyroxine', 'Moderate', 'INTERVAL: Thyroid preparations may interfere with the uptake, effects, and/or diagnostic results of radioiodides.', 'DDInter', 1767317476, 'Liothyronine (triiodothyronine) should be withheld for at least 10 days and levothyroxine (thyroxine) for 6 weeks before administering sodium iodide I-131 or I-123, and for 2 weeks after treatment.', 'Absorption', 'Liothyronine (triiodothyronine) should be withheld for at least 10 days and levothyroxine (thyroxine) for 6 weeks before administering sodium iodide I-131 or I-123, and for 2 weeks after treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423761, 'Tolevamer', 'Levothyroxine', 'Moderate', 'INTERVAL: Ion-exchange resins may bind to and decrease the gastrointestinal absorption of thyroid hormones.', 'DDInter', 1767317476, 'Thyroid hormone preparations and ion-exchange resins should be administered at least 4 hours apart. Close clinical and laboratory monitoring of thyroid function is recommended during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'Thyroid hormone preparations and ion-exchange resins should be administered at least 4 hours apart.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423762, 'Sorafenib', 'Levothyroxine', 'Moderate', 'Sorafenib impairs exogenous thyroid suppression in differentiated thyroid carcinoma (DTC).', 'DDInter', 1767317476, 'TSH levels should be monitored monthly during sorafenib treatment for differentiated thyroid carcinoma, and the dosage of thyroid replacement medication adjusted as necessary.', 'Antagonism', 'TSH levels should be monitored monthly during sorafenib treatment for differentiated thyroid carcinoma, and the dosage of thyroid replacement medication adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
