INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423764, 'Stanozolol', 'Levothyroxine', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, NULL, 'Moderate', 'pharmacodynamic');
