INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423851, 'Thyroid, porcine', 'Methimazole', 'Moderate', 'Antithyroid agents such as methimazole and propylthiouracil can affect the therapeutic response to thyroid hormone therapy. These agents decrease thyroid hormone secretion, which may result in hypothyroidism. Conversely, thyroid hormone therapy can antagonize the pharmacologic effects of antithyroid agents by supplying an exogenous source of the hormone.', 'DDInter', 1767317476, 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', 'Antagonism', 'Concomitant use of antithyroid agents and thyroid hormones should generally be avoided due to mutually negating effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423852, 'Thyroid, porcine', 'Methyltestosterone', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, NULL, 'Moderate', 'pharmacodynamic');
