INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423891, 'Tolevamer', 'Thyroid, porcine', 'Moderate', 'INTERVAL: Ion-exchange resins may bind to and decrease the gastrointestinal absorption of thyroid hormones.', 'DDInter', 1767317476, 'Thyroid hormone preparations and ion-exchange resins should be administered at least 4 hours apart. Close clinical and laboratory monitoring of thyroid function is recommended during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'Thyroid hormone preparations and ion-exchange resins should be administered at least 4 hours apart.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423892, 'Thyroid, porcine', 'Sorafenib', 'Moderate', 'Sorafenib impairs exogenous thyroid suppression in differentiated thyroid carcinoma (DTC).', 'DDInter', 1767317476, 'TSH levels should be monitored monthly during sorafenib treatment for differentiated thyroid carcinoma, and the dosage of thyroid replacement medication adjusted as necessary.', 'Antagonism', 'TSH levels should be monitored monthly during sorafenib treatment for differentiated thyroid carcinoma, and the dosage of thyroid replacement medication adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423893, 'Thyroid, porcine', 'Sotalol', 'Minor', 'Thyroid hormone therapy may reverse decreased hepatic blood flow associated with hypothyroidism. Increased hepatic metabolism and decreased serum levels of some beta-blockers may result. Data are available for propranolol only. No special precautions are necessary. When hypothyroidism is converted to a euthyroid state, a decrease in beta-blocking effectiveness is possible.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
