INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167087, 'Fosphenytoin', 'Stiripentol', 'Major', 'Coadministration with potent inducers of CYP450 1A2, 2C19, and/or 3A4 may significantly decrease the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways. Pharmacokinetic studies have not been conducted. Stiripentol itself is metabolized by these pathways and has been reported to also inhibit multiple CYP450 isoenzymes in vitro, thus both competitive and noncompetitive inhibition may occur. Coadministration of stiripentol with other anticonvulsants may increase central nervous system adverse effects such as somnolence, dizziness, confusion, difficulty concentrating, and impairment of psychomotor skills.', NULL, 'Concomitant use of stiripentol with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, and primidone should generally be avoided.', NULL, 'Concomitant use of stiripentol with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, and primidone should generally be avoided. If coadministration is required, patients should have plasma anticonvulsant concentrations monitored and dosage adjustments made accordingly. Patients should also be monitored for increased adverse effects such as dizziness, drowsiness, lethargy, confusion, diplopia, nystagmus, ataxia, dysarthria, hypothermia, and hypotension.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167262/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Tran A, Vauzellekervroedan F, Rey E, Pons G, Dathis P, Chiron C, Dulac O, Renard F, Olive G "Effect of stiripentol on carbamazepine plasma concentration and metabolism in epileptic children." Eur J Clin Pharmacol 50 (1996): 497-500[3] May TW, Boor R, Mayer T, et al. "Concentrations of stiripentol in children and adults with epilepsy: the influence of dose, age, and comedication." Ther Drug Monit 34 (2012): 390-7[4] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.[5] Cazali N, Tran A, Treluyer JM, et al. "Inhibitory effect of stiripentol on carbamazepine and saquinavir metabolism in human." Br J Clin Pharmacol 56 (2003): 526-36[6] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[7] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[8] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[9] "Product Information. Diacomit (stiripentol)." Biocodex Inc, Creswell, OR.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167088, 'Fostamatinib', 'Stiripentol', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167263/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767365209);
