INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423894, 'Thyroid, porcine', 'Stanozolol', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', 'DDInter', 1767317476, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423895, 'Thyroid, porcine', 'Sucralfate', 'Minor', 'Limited clinical data suggest that sucralfate may interfere with the absorption of thyroid hormone in some patients. The clinical significance of this interaction is unknown. Administering sucralfate twice a day instead of four times a day and separating thyroid supplements from sucralfate by eight hours may avoid a potential interaction. The patient''s clinical response should be monitored.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423896, 'Iron sucrose', 'Thyroid, porcine', 'Moderate', 'Coadministration with sucroferric oxyhydroxide may reduce the oral bioavailability of levothyroxine. The proposed mechanism involves nonspecific adsorption of levothyroxine to the ferric ion component of sucroferric oxyhydroxide, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract. It is not known whether this interaction occurs with other thyroid hormone preparations.', 'DDInter', 1767317476, 'Oral formulations of levothyroxine should not be prescribed with sucroferric oxyhydroxide.', 'Absorption', 'Oral formulations of levothyroxine should not be prescribed with sucroferric oxyhydroxide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
