INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423976, 'Fenfluramine', 'Linagliptin', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317476, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423977, 'Fosinopril', 'Linagliptin', 'Moderate', 'Limited clinical data suggest that dipeptidyl peptidase IV (DPP IV) inhibitors, e.g., gliptins, have been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema, such as angiotensin converting enzyme (ACE) inhibitors may theoretically increase the risk.', 'DDInter', 1767317476, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing. Close monitoring for the development of hypoglycemia is recommended if ACE inhibitors are coadministered with inhibitors, particularly in patients with advanced age and/or renal impairment and during the first month of treatment. Dosage adjustment may be required if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs.', 'Synergism', 'Clinicians and patients should be aware of the potential for increased risk of angioedema when inhibitors are prescribed with ACE inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
