INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1423992, 'Ramipril', 'Metformin', 'Moderate', 'Limited data suggest that ACE inhibitors may potentiate the hypoglycemic effects of oral antidiabetic drugs, including metformin. The mechanism is unknown. Symptomatic and sometimes severe hypoglycemia has occurred.', 'DDInter', 1767317476, 'Close monitoring for the development of hypoglycemia is recommended if ACE inhibitors are coadministered with metformin, particularly in patients with advanced age and/or renal impairment. Dosage adjustments may be required if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their physician if it occurs. Patients should be observed for loss of glycemic control when ACE inhibitors are withdrawn.', 'Others', 'Close monitoring for the development of hypoglycemia is recommended if ACE inhibitors are coadministered with metformin, particularly in patients with advanced age and/or renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
