INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424084, 'Lisdexamfetamine', 'Liothyronine', 'Moderate', 'The concomitant use of thyroid hormones, especially large doses, with sympathomimetic drugs may potentiate sympathomimetic effects and increase the risk of cardiac insufficiency in patients who have coronary artery disease.', 'DDInter', 1767317476, 'Monitoring for increased sympathomimetic effects and cardiac function may be advisable.', 'Synergism', 'Monitoring for increased sympathomimetic effects and cardiac function may be advisable.', NULL, NULL, 'Moderate', 'pharmacodynamic');
