INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424103, 'Nortriptyline', 'Liothyronine', 'Moderate', 'Coadministration of thyroid hormone replacement therapy with tricyclic antidepressants may accelerate the onset or potentiate the action of tricyclic antidepressants, increasing the risk of cardiac arrhythmias and CNS stimulation. The proposed mechanism may be an increased receptor sensitivity to catecholamines. Some clinicians have used this interaction therapeutically.', 'DDInter', 1767317476, 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation. Advise patients to contact their doctor if they experience toxicity symptoms such as: anxiety, agitation, insomnia, shortness of breath, irregular or fast heartbeat, and lightheadedness or dizziness.', 'Synergism', 'Patients receiving concomitant thyroid hormone replacement therapy and tricyclic antidepressant therapy should be closely monitored for cardiac arrhythmias and CNS stimulation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424104, 'Orlistat', 'Liothyronine', 'Moderate', 'INTERVAL: Orlistat may interfere with the gastrointestinal absorption of levothyroxine and other thyroid hormones. The exact mechanism of interaction is unknown.', 'DDInter', 1767317476, 'Orlistat and oral thyroid hormone preparations should be administered at least 4 hours apart. Close clinical and laboratory monitoring of thyroid function is recommended. Patients should be advised to contact their physician if they experience symptoms of hypothyroidism such as fatigue, muscle weakness or pain, joint stiffness, depression, constipation, hoarseness, weight gain, hair loss, brittle nails, pale or dry skin, cold intolerance, memory problems, confusion, or menstrual irregularities.', 'Absorption', 'Orlistat and oral thyroid hormone preparations should be administered at least 4 hours apart.', NULL, NULL, 'Moderate', 'pharmacodynamic');
