INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424150, 'Warfarin', 'Liothyronine', 'Moderate', 'Since thyroid hormones may increase the catabolism of vitamin K-dependent clotting factors, the hypoprothrombinemic response to oral anticoagulants may be enhanced during the initiation of thyroid hormone therapy.', 'DDInter', 1767317476, 'INR or prothrombin time should be monitored closely when initiating, discontinuing, or changing the dosage of thyroid hormone therapy in patients stabilized on their oral anticoagulant regimen. Patients should be advised to notify their physician if they develop signs and symptoms of excessive anticoagulation, such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Others', 'INR or prothrombin time should be monitored closely when initiating, discontinuing, or changing the dosage of thyroid hormone therapy in patients stabilized on their oral anticoagulant regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
