INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424156, 'Butabarbital', 'Liotrix', 'Moderate', 'Barbiturates may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. Clinical data are limited.', 'DDInter', 1767317476, 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of thyroid replacement dosage may be indicated when initiating or discontinuing barbiturate therapy.', 'Metabolism', 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424157, 'Calcium carbonate', 'Liotrix', 'Moderate', 'INTERVAL: Concurrent administration of calcium-containing products may decrease the oral bioavailability of levothyroxine by one-third in some patients. Pharmacologic effects of levothyroxine may be reduced. The exact mechanism of interaction is unknown but may involve nonspecific adsorption of levothyroxine to calcium at acidic pH levels, resulting in an insoluble complex that is poorly absorbed from the gastrointestinal tract.', 'DDInter', 1767317476, 'Some experts recommend separating the times of administration of levothyroxine and calcium-containing preparations by at least 4 hours. Monitoring of serum TSH levels is recommended. Patients with gastrointestinal or malabsorption disorders may be at a greater risk of developing clinical or subclinical hypothyroidism due to this interaction.', 'Absorption', 'Some experts recommend separating the times of administration of levothyroxine and calcium-containing preparations by at least 4 hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
