INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167503, 'Vemurafenib', 'Sulfasalazine', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as dabigatran, rosuvastatin, sulfasazine, and topotecan. The proposed mechanism, based on in vitro data, is decreased clearance due to vemurafenib-mediated inhibition of P-gp and/or BCRP efflux transport proteins.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if vemurafenib is used concomitantly with drugs that are substrates of P-gp and/or BCRP transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever vemurafenib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Others', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, More', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Trichlormethiazide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167678/', '[1] "Product Information. Zelboraf (vemurafenib)." Genentech, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167504, 'Verteporfin', 'Sulfasalazine', 'Moderate', 'Concomitant use of verteporfin with other known photosensitizing agents may increase the risk of photosensitivity reactions. Medicinal products with known phototoxic or photoallergic potential include fluoroquinolones, phenothiazines, retinoids, sulfonamides, sulfonylureas, tetracyclines, thiazide diuretics, griseofulvin, and hypericin extracts (e.g., St John''s Wort).', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided.', NULL, 'Caution is advised and pharmacologic response to photodynamic therapy should be carefully monitored if concomitant use of other photosensitizing agents cannot be avoided. Patients should be advised to avoid exposure of skin or eyes to sunlight or bright indoor lights (e.g., examination lamps, operating room lamps, tanning beds, lights at close proximity) for 5 days after administration of verteporfin.', 'Synergism', 'Aflibercept, Bevacizumab', 'Tinidazole, Ascorbic acid, Metronidazole, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, Darunavir, Ketoconazole, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167679/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Visudyne (verteporfin)" Valeant Pharmaceuticals, Costa Mesa, CA.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition."