INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167570, 'Irbesartan', 'Sulindac', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', 'Chondroitin sulfate, Esomeprazole, Glucosamine', 'Sacubitril', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167746/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167571, 'Sulindac', 'Lepirudin', 'Moderate', 'Theoretically, the coadministration of nonsteroidal anti-inflammatory drugs (NSAIDs) and thrombin inhibitors may potentiate the risk of bleeding. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals.', NULL, 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors.', NULL, 'Until further data are available, products containing NSAIDs, especially if given chronically or in high dosages, should preferably be avoided in patients receiving thrombin inhibitors. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167747/', '[1] "Product Information. Angiomax (bivalirudin)" The Medicines Compny, Cambridge, MA.[2] "Product Information. Acova (argatroban)" SmithKline Beecham, Philadelphia, PA.[3] "Product Information. Refludan (lepirudin)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167572, 'Sulindac', 'Lomefloxacin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', 'Rabeprazole, Phenylbutazone, Misoprostol, Glucosamine, Chondroitin sulfate, Esomeprazole', 'Tetracycline, Ganciclovir, Rifamycin, Oxytetracycline, Polymyxin B, Famciclovir, Sulfamethizole, Cefuroxime, Trifluridine, Benzylpenicillin, Ampicillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167748/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." 