INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167606, 'Tinzaparin', 'Sulindac', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167782/', '[1] Weale AE, Warwick DJ, Durant N, Prothero D "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 35-7[2] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[6] Walker AM "Predictors of bleeding during heparin therapy." JAMA 244 (1980): 1209-12[7] Klinkhardt U, Breddin HK, Esslinger HU, Haas S, Kalatzis A, Harder S "Interaction between the LMWH reviparin and aspirin in healthy volunteers." Br J Clin Pharmacol 49 (2000): 337-41[8] Heiden D, Rodvien R, Mielke CH "Heparin bleeding, platelet dysfunction, and aspirin." JAMA 246 (1981): 330-1[9] Theroux P, Ouimet H, McCans J, et al. "Aspirin, heparin, or both to treat acute unstable angina." N Engl J Med 319 (1988): 1105-6[10] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395[11] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[12] Bang CJ, Riedel B, Talstad I, Berstad A "Interaction between heparin and acetylsalicylic acid on gastric mucosal and skin bleeding in humans." Scand J Gastroenterol 27 (1992): 489-94', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167607, 'Tirofiban', 'Sulindac', 'Moderate', 'The risk of bleeding associated with glycoprotein IIb/IIIa inhibitors is increased in the presence of nonsteroidal anti-inflammatory agents due to additive inhibitory effects on platelet function. Arterial puncture sites may be most problematic.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', NULL, 'It is recommended that these drugs be used together only when indicated by skilled personnel who are qualified and equipped to detect and treat bleeding complications.', 'Synergism', 'Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Mefenamic acid', 'Protein C, Acenocoumarol, Selexipag', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167783/', '[1] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167608, 'Tobramycin', 'Sulindac', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Misoprostol, Chondroitin sulfate, Glucosamine', 'Tetracycline, Ganciclovir, Rifamycin, Oxytetracycline, Polymyxin B, Famciclovir, Sulfamethizole, Cefuroxime, Trifluridine, Benzylpenicillin, Ampicillin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167784/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac