INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167663, 'Esmolol', 'Sunitinib', 'Moderate', 'There is clinical evidence that sunitinib can prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, calcium channel blockers, atazanavir, lopinavir, digoxin, lacosamide, mefloquine) may result in additive effects and increased risk of conduction disturbances and atrioventricular block.', NULL, 'Caution is advised if sunitinib is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.', NULL, 'Caution is advised if sunitinib is used concomitantly with other agents that prolong the PR interval, especially in the elderly and patients with known conduction problems (e.g., marked first-degree AV block; second-degree or higher AV block; sick sinus syndrome without pacemaker) or severe cardiac disease such as myocardial ischemia or heart failure. Patients should be advised to notify their doctor if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167839/', '[1] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[2] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[3] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167664, 'Etidronic acid', 'Sunitinib', 'Moderate', 'Osteonecrosis of the jaw (ONJ) has been reported with the use of angiogenesis inhibitors including denosumab, bevacizumab, and sunitinib. Concomitant exposure with other risk factors such as bisphosphonates (potency, route of administration, and cumulative dose), cancer, chemotherapy, corticosteroids, radiotherapy to the head and neck, dental disease, invasive dental procedures, or poor oral hygiene may increase the risk. Most reported cases with bevacizumab have occurred in patients who received prior or concomitant treatment with intravenous bisphosphonates or had a history of dental disease requiring invasive dental procedures.', NULL, 'Caution is advised if bisphosphonates are used simultaneously or sequentially with denosumab or bevacizumab.', NULL, 'Caution is advised if bisphosphonates are used simultaneously or sequentially with denosumab or bevacizumab. Prior to initiating treatment, a thorough dental examination and appropriate preventive dentistry should be considered. If possible, invasive dental procedures should be avoided in patients who have previously received or are receiving intravenous bisphosphonates. Good oral hygiene practices should be maintained during treatment. Patients should be advised to seek medical attention if they experience signs and symptoms of osteonecrosis in the jaw, such as: pain in the mouth, teeth, or jaw, swelling or sores inside the mouth, numbness or a feeling of heaviness in the jaw, loosening of a tooth, or exposure of bone in the jaw. Temporary interruption of treatment should be considered if clinically appropriate, until the condition resolves.', 'Synergism', 'Cholecalciferol, Vosoritide, Denosumab, Burosumab', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167840/', '[1] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Prolia (denosumab)." Amgen USA, Thousand Oaks, CA.[5] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
