INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167706, 'Indinavir', 'Sunitinib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme. Because sunitinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib.', NULL, 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib. Some authorities recommend avoiding concomitant use of sunitinib during and for 2 weeks after treatment with itraconazole. A dose reduction for sunitinib to a minimum of 37.5 mg daily for gastrointestinal stromal tumor (GIST) and metastatic renal cell carcinoma (mRCC) or 25 mg daily for pancreatic neuroendocrine tumors (pNET) should be considered if it must be coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Futibatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167882/', '[1] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[5] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[6] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167707, 'Insulin human', 'Sunitinib', 'Moderate', 'The hypoglycemic effect of insulin may be potentiated by certain drugs, including ACE inhibitors, angiotensin receptor blockers (ARBs), 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinidine, quinine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, ARBs, fibrates, ginseng); stimulating insulin secretion (salicylates, disopyramide, pentoxifylline, propoxyphene, quinidine, quinine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Clinical hypoglycemia has been reported during use of some of these agents alone or with insulin and/or insulin secretagogues.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment.', NULL, 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin, particularly in patients with advanced age and/or renal impairment. The insulin dosage may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations).', 'Synergism', 'Liraglutide, Lixisenatide', 'Entrectinib, Imatinib, Pralsetinib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167883/', '[1] "Product Information. Apidra (insulin glulisine)." Aventis Pharmaceuticals, Bridgewater, NJ.[2] Semel JD, Wortham E, Karl DM "Fasting hypoglycemia associated with disopyramide." Am Heart J 106 (1983): 1160-1[3] Asplund K, Wiholm BE, Lithner F "Glibenclamide-associated hypoglycaemia: a report on 57 cases." Diabetologia 24 (1983): 412-7[4] Goldberg IJ, Brown LK, Rayfield EJ "Disopyramide (norpace)-induced hypoglycemia." Am J Med 69 (1980): 463-6[5] Croxson MS, Shaw DW, Henley PG, Gabriel HDLL "Disopyramide-induced hypoglycaemia and increased serum insulin." N Z Med J July (1987): 407-8[6] Herings RMC, Deboer A, Stricker BHC, Leufkens HGM, Porsius A "Hypoglycaemia associated with use of inhibitors of angiotensin converting enzyme." Lancet 345 (1995): 1195-8[7] Giugliano D, Ceriello A, Saccomanno F, et al "Effects of salicylate, tolbutamide, and prostaglandin E2 on insulin responses to glucose in noninsulin-dependent diabetes mellitus." J Clin Endocrinol Metab 61 (1985): 160-6[8] Aleyassine H, Gardiner RJ "Dual action of antidepressant drugs (MAO inhibitors) on insulin release." Endocrinology 96 (1975): 702-10[9] Baciewicz AM, Swafford WB Jr "Hypoglycemia induced by the interaction of chlorpropamide and co-trimoxazole." Drug Intell Clin Pharm 18 (1984): 309-10[10] Hellman B "Potentiating effects of drugs on the binding of glibenclamide to pancreatic beta cells." Metabolism 23 (1974): 839-46[11] Christensen LK, Hansen JM, Kristensen M "Sulphaphenazole-induced hypoglycemic attacks in tolbutamide-treated diabetics." Lancet 2 (1963): 1298-301[12] Darcy PF, Griffin JP "Interac