INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (167773, 'Troleandomycin', 'Sunitinib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme. Because sunitinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', NULL, 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib.', NULL, 'Alternative agents with no or minimal CYP450 3A4-inhibiting activity are recommended in patients treated with sunitinib. Some authorities recommend avoiding concomitant use of sunitinib during and for 2 weeks after treatment with itraconazole. A dose reduction for sunitinib to a minimum of 37.5 mg daily for gastrointestinal stromal tumor (GIST) and metastatic renal cell carcinoma (mRCC) or 25 mg daily for pancreatic neuroendocrine tumors (pNET) should be considered if it must be coadministered with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, voriconazole, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 'Dirithromycin, Lincomycin, Clindamycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/167949/', '[1] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Mu