    ) VALUES (1424205, 'Liotrix', 'Ketamine', 'Moderate', 'Isolated case reports suggest that the concomitant use of ketamine and thyroid hormones may result in hypertension and tachycardia. The mechanism is unknown.', NULL, 'Caution is recommended if ketamine is administered to patients receiving thyroid hormone therapy.', NULL, 'Caution is recommended if ketamine is administered to patients receiving thyroid hormone therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
