        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424208, 'Liotrix', 'Lanthanum carbonate', 'Moderate', 'INTERVAL: The coadministration of phosphate binders, such as sevelamer and lanthanum, with levothyroxine may increase levels of thyroid stimulating hormone (TSH). The mechanism appears to involve binding of levothyroxine to the phosphate binder resulting in delayed or lack of levothyroxine absorption.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders.', NULL, 'Levothyroxine should be administered at least 4 hours apart from phosphate binders. It is recommended that TSH levels be monitored more closely during concomitant use.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
