        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424294, 'Aminophylline', 'Ritonavir', 'Moderate', 'The coadministration with ritonavir may decrease the plasma concentrations of theophylline. The proposed mechanism is ritonavir induction of CYP450 1A2, the isoenzyme responsible for the metabolic clearance of theophylline.', NULL, 'During coadministration with ritonavir, the possibility of a diminished therapeutic response to theophylline should be considered.', NULL, 'During coadministration with ritonavir, the possibility of a diminished therapeutic response to theophylline should be considered. Dosage adjustment as well as clinical and laboratory monitoring may be appropriate whenever ritonavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
