        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424326, 'Chloramphenicol', 'Lopinavir', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lopinavir, which is primarily metabolized by the isoenzyme. The magnitude and clinical significance of this interaction are unknown, particularly when in the presence of ritonavir as a pharmacokinetic booster. Limited data indicate that ketoconazole, a potent CYP450 3A4 inhibitor, does not substantially alter the plasma concentrations of lopinavir administered as lopinavir-ritonavir in a 4:1 ratio.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
