    ) VALUES (1424363, 'Eravacycline', 'Ritonavir', 'Minor', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of eravacycline, which undergoes oxidation by CYP450 3A4 and flavin monoxygenase (FMO).', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
