        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424380, 'Etravirine', 'Ritonavir', 'Major', 'Coadministration with full-dose ritonavir (600 mg twice a day) may significantly decrease the plasma concentrations of etravirine. The mechanism of interaction has not been described but may involve ritonavir induction of CYP450 2C19 and 2C9, of which etravirine is a substrate.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with full-dose ritonavir should be avoided.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the use of etravirine in combination with full-dose ritonavir should be avoided. Etravirine may be used with low-dose ritonavir administered as a pharmacokinetic booster in certain protease inhibitor regimens.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
