        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424492, 'Lopinavir', 'Lenvatinib', 'Moderate', 'Lopinavir in combination with ritonavir may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of lopinavir-ritonavir with other drugs that can prolong the QT interval should generally be avoided. Patients treated with any medication that can cause QT prolongation should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424493, 'Ritonavir', 'Letermovir', 'Moderate', 'Coadministration with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes may decrease the plasma concentrations of letermovir according to the product labeling. Because letermovir is a substrate of P-gp and UGT 1A1 and 1A3, induction of the efflux transporter and metabolic enzymes may theoretically increase the clearance of letermovir.', NULL, 'Due to the potential loss of efficacy, use of letermovir with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes is not recommended.', NULL, 'Due to the potential loss of efficacy, use of letermovir with inducers of the P-glycoprotein (P-gp) efflux transporter and/or the uridine diphosphate glucuronosyltransferase (UGT) enzymes is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
