INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168057, 'Iloperidone', 'Tacrolimus', 'Major', 'Iloperidone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of iloperidone with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged. Persistent QTc measurements exceeding 500 msec will require suspension of iloperidone therapy and immediate action to correct any concomitant risk factors before resuming treatment.', 'Synergism', 'Deoxycholic acid, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, Cromoglicic acid, Abrocitinib, More', 'Thiothixene, Loxapine, Molindone, Cariprazine, Lurasidone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168233/', '[1] "Product Information. Exforge HCT (amlodipine/hydrochlorothiazide/valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/