INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168093, 'Tacrolimus', 'Methoxyflurane', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Cromoglicic acid, More', 'Salicylic acid, Choline salicylate, Ziconotide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168269/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sheiner PA, Mor E, Chodoff L, Glabman S, Emre S, Schwartz ME, Miller CM "Acute renal, failure associated with the use of ibuprofen in two liver transplant recipients on FK506." Transplantation 57 (1994): 1132-3[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168094, 'Tacrolimus', 'Mumps virus strain B level jeryl lynn live antigen', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Ivermectin, Caffeine, Diclofenac, Cromoglicic acid, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'ht