INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168131, 'Terbutaline', 'Tacrolimus', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Ephedrine, Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Mometasone, Cromoglicic acid, Ipratropium, Beclomethasone dipropionate', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168307/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hano