INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168138, 'Tiagabine', 'Tacrolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Ethotoin, Brivaracetam, Methsuximide, Paramethadione, Fenfluramine, Perampanel, Eslicarbazepine, Phensuximide, Ganaxolone, Phenacemide, Trimethadione, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168314/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[7] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168139, 'Ticagrelor', 'Tacrolimus', 'Moderate', 'Coadministration with ticagrelor may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme. The mechanism is reduced clearance due to inhibition of CYP450 3A4 by ticagrelor, which is a mild inhibitor. The interaction may be significant for sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index.', NULL, 'Concomitant use of ticagrelor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, 'Concomitant use of ticagrelor with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., cisapride, ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, pimozide, triazolam, vinca alkaloids) is not recommended.', 'Metabolism', 'Minoxidil, Calcium gluconate, Pimecrolimus, Magnesium sulfate, Glycopyrronium, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Cromoglicic acid, Dupilumab, More', 'Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, Tinzaparin, Selexipag, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168315/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168140, 'Tinidazole', 'Tacrolimus', 'Moderate', 'Coadministration with a nitroimidazole may increase the blood concentrations of tacrolimus. The interaction has been reported with metronidazole, and the proposed mechanism is inhibition of hepatic tacrolimus metabolism via CYP450 3A4.', NULL, 'Caution is advised if tacrolimus is used with metronidazole.', NULL, 'Caution is advised if tacrolimus is used with metronidazole. Tacrolimus blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of metronidazole therapy in patients who are stabilized on their tacrolimus regimen. Given its structural similarities to metronidazole, the same precaution may be applicable during therapy with tinidazole, although clinical data are lacking.', 'Metabolism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Cefixime, Secnidazole, Bismuth subcitrate potassium, Oritavancin, Daptomycin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin, Ascorbic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168316/', '[1] Page RL, Klem PM, Rogers C "Potential elevation of tacrolimus trough concentrations with concomitant metronidazole therapy." Ann Pharmacother 39 (2005): 1109-13[2] "Product Information. Tindamax (tinidazole)." Presutti Laboratories Inc, Arlington Heights, IL.[3] "Product Information. Flagyl (metronidazole)." Searle, Skokie, IL.[4] Herzig K, Johnson DW "Marked elevation of blood cyclosporine 