INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168143, 'Tobramycin', 'Tacrolimus', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', NULL, 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Rifamycin, Oxytetracycline, Sulfamethizole, Trifluridine, Benzylpenicillin, Sulfisoxazole, Povidone-iodine', 'Minoxidil, Deoxycholic acid, Pimecrolimus, Calcium gluconate, Glycopyrronium, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Cromoglicic acid, Abrocitinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168319/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Sheiner PA, Mor E, Chodoff L, Glabman S, Emre S, Schwartz ME, Miller CM "Acute renal, failure associated with the use of ibuprofen in two liver transplant recipients on FK506." Transplantation 57 (1994): 1132-3[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168144, 'Tocilizumab', 'Tacrolimus', 'Moderate', 'The use of interleukin blockers with other immunosuppressive or myelosuppressive agents may increase the risk of infections. Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin inhibitors in patients with chronic inflammatory diseases.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible.', NULL, 'Concomitant use of interleukin blockers with other immuno- or myelosuppressive agents should be avoided if possible. Caution is advised when interleukin inhibitors are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges such as immunosuppressants or antineoplastic agents. Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of interleukin inhibitor therapy, and the dosage(s) of these drugs adjusted accordingly. Clinicians should note that the effects of interleukin inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168320/', '[1] "Product Information. Arcalyst (rilonacept)." Regeneron Pharmaceuticals Inc, Tarrytown, NY.[2] "Product Information. Sylvant (siltuximab)." Janssen Biotech, Inc., Horsham, PA, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Actemra (tocilizumab)." Genentech, South San Francisco, CA.[5] "Product Information. Stelara (ustekinumab)." Centocor Inc, Malvern, PA.[6] "Product Information. Amevive (alefacept)." Biogen, Cambridge, MA.[7] "Product Information. Taltz Autoinjector (ixekizumab)." Eli Lilly and Company, Indianapolis, IN.[8] "Product Information. Cosentyx (secukinumab)." Novartis Pharmaceuticals, East Hanover, NJ.[9] "Product Information. Ilaris (canakinumab)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (168145, 'Tofacitinib', 'Tacrolimus', 'Major', 'Coadministration of tofacitinib with other immunosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.', NULL, 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.g., azathioprine, cyclosporine). Patients receiving tofacitinib should be closely monitored for the development of signs and symptoms of infection during and after treatment, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Ivermectin, Caffeine, Cromoglicic acid, Finasteride, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/168321/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
