INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424648, 'Procainamide', 'Lopinavir', 'Major', 'Class IA (e.g., disopyramide, quinidine, procainamide) and class III (e.g., amiodarone, dofetilide, sotalol) antiarrhythmic agents can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks. Caution and clinical monitoring are recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of class IA or class III antiarrhythmic agents with other drugs that can prolong the QT interval should preferably be avoided unless benefits are anticipated to outweigh the risks.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424649, 'Ritonavir', 'Proguanil', 'Moderate', 'Coadministration with ritonavir may decrease the plasma concentrations of atovaquone and proguanil. The mechanism of interaction has not been established, but may involve ritonavir induction of the glucuronidation of atovaquone and the CYP450 2C19-mediated metabolism of proguanil.', 'DDInter', 1767317476, 'The potential for treatment failure should be considered when atovaquone or atovaquone/proguanil is used with ritonavir. Some experts recommend avoiding concomitant use. If coadministration is required, an increased dosage of atovaquone and proguanil may be required.', 'Metabolism', 'The potential for treatment failure should be considered when atovaquone or atovaquone/proguanil is used with ritonavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
