INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424652, 'Ritonavir', 'Propafenone', 'Major', 'Coadministration with ritonavir may significantly increase the plasma concentrations of some antiarrhythmic agents such as amiodarone, bepridil, flecainide, propafenone, and quinidine. The proposed mechanism is ritonavir inhibition of CYP450 3A4 and/or 2D6, the isoenzymes responsible for the metabolic clearance of these agents. Although clinical data are lacking, the interaction could conceivably lead to serious and/or life-threatening reactions including QT interval prolongation and ventricular arrhythmias such as ventricular tachycardia and torsade de pointes.', 'DDInter', 1767317476, 'The use of ritonavir with amiodarone, bepridil, flecainide, propafenone, or quinidine is considered contraindicated.', 'Metabolism', 'The use of ritonavir with amiodarone, bepridil, flecainide, propafenone, or quinidine is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
