INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424654, 'Propofol', 'Lopinavir', 'Moderate', 'Treatment with propofol may lead to prolongation of the QT interval; however, the extent of prolongation and its clinical impact is difficult to determine.', 'DDInter', 1767317476, 'Caution and clinical monitoring is recommended if propofol is used concomitantly with other agents associated with QT interval prolongation. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring is recommended if propofol is used concomitantly with other agents associated with QT interval prolongation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1424655, 'Dextropropoxyphene', 'Ritonavir', 'Moderate', 'Ritonavir inhibits CYP450 isoenzymes and may interfere with the metabolism of propoxyphene. Clinical data have not been reported; however, the accumulation of propoxyphene may result in excessive CNS- and/or respiratory-depressant effects. Excessive doses of propoxyphene, either alone or in combination with other CNS depressants, have been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse. In a large Canadian study, propoxyphene use was also associated with a 60% increased risk of hip fracture in the elderly.', 'DDInter', 1767317476, 'During concomitant administration, monitoring for excessive side effects of propoxyphene may be advisable. Dose reductions may be considered if an interaction is suspected.', 'Metabolism', 'During concomitant administration, monitoring for excessive side effects of propoxyphene may be advisable.', NULL, NULL, 'Moderate', 'pharmacodynamic');
