        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424853, 'Fenoldopam', 'Losartan', 'Moderate', 'The use of fenoldopam with other hypotensive agents has not been studied. However, these combinations would theoretically increase the risk of severe symptomatic hypotension.', NULL, 'Caution is strongly advised during concurrent use.', NULL, 'Caution is strongly advised during concurrent use. Inpatient monitoring of the heart rate and blood pressure is recommended. Sublingual nitroglycerin has been used safely with fenoldopam.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424854, 'Fluconazole', 'Losartan', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentration of losartan but decrease that of its active carboxylic acid metabolite. The mechanism is fluconazole inhibition of CYP450 2C9, the isoenzyme that mediates the conversion of losartan to the metabolite.However, since the active metabolite is 10 to 40 times more potent by weight than losartan and is responsible for most of the angiotensin II receptor antagonism that follows losartan treatment, the potential for diminished pharmacologic effect should be considered. Fluconazole had no effect on the pharmacokinetics of eprosartan, another angiotensin II receptor antagonist.', NULL, 'If fluconazole is to be given for more than a few doses, patients should consider having blood pressure monitored more closely following initiation, discontinuation or change of dosage of fluconazole.', NULL, 'If fluconazole is to be given for more than a few doses, patients should consider having blood pressure monitored more closely following initiation, discontinuation or change of dosage of fluconazole. Use of an alternative antifungal agent which does not inhibit CYP450 2C9 (e.g., itraconazole, ketoconazole, terbinafine) or another angiotensin II receptor antagonist such as eprosartan may be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
