        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1424930, 'Lumacaftor', 'Apixaban', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Concomitant use of apixaban with dual P-gp and potent CYP450 3A4 inducers should generally be avoided, since efficacy of apixaban may be compromised.', NULL, 'Concomitant use of apixaban with dual P-gp and potent CYP450 3A4 inducers should generally be avoided, since efficacy of apixaban may be compromised.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
